<?phpclass C_Article extends C_Base{	private $article_title;		// заголовок статьи	private $article_content;		// тело страницы	//	// Конструктор.	//	function __construct()	{			}		//	// Виртуальный обработчик запроса.	//	protected function OnInput()	{					$m_article = M_Article::Instance();		$this->title = "Blog OOP ";		$this->title2 = $this->title2.":: Article ";		if(isset($_GET['id']))		{			$id=mysql_real_escape_string($_GET['id']);			$article=$m_article->get($id);			$this->article_title=$article['title'];			$this->article_content=$article['content'];		}			}		//	// Виртуальный генератор HTML.	//		protected function OnOutput()	{				$vars = array('title' => $this->article_title, 'content' => $this->article_content);			//внунтенний шаблон		$this->content = $this->Template('v/V_Article.php', $vars);						parent::OnOutput();	}	}