<?phpinclude_once('c/Controller.php');//// Базовый контроллер сайта.//abstract class C_Base extends Controller{	protected $title;		// тайтл страницы	protected $title2="Blog 1.3";      // заголовок на странице	protected $content;		// содержание страницы	//	// Конструктор.	//	function __construct()	{				}		//	// Виртуальный обработчик запроса.	//	protected function OnInput()	{			}		//	// Виртуальный генератор HTML. ВНЕШНИЙ ШАБЛОН	//		protected function OnOutput()	{	// Кодировка.				$vars = array('title' => $this->title,'title2' => $this->title2, 'content' => $this->content);			$page = $this->Template('v/V_Main.php', $vars);						echo $page;	}	}