<?phpclass C_Editor extends C_Base{	private $articles;		// все статьи		//	// Конструктор.	//	function __construct()	{			}		//	// Виртуальный обработчик запроса.	//	protected function OnInput()	{		$m_article= M_Article::Instance();				$this->title = "Blog OOP ";		$this->title2 = $this->title2.":: Editor ";		$this->articles = $m_article->all();	}		//	// Виртуальный генератор HTML.	//		protected function OnOutput()	{		$vars = array('articles' => $this->articles);			$this->content = $this->Template('v/V_Editor.php', $vars);						parent::OnOutput();	}	}