<?phpclass C_IndexArticle extends C_Base{	private $articles;	private $article;	private $narticles;	function __construct(){			}			// обработчик запроса	protected function OnInput(){				$m_article= M_Article::Instance();				$this->title="Blog OOP ";		$this->title2=$this->title2.':: Index page';		$this->articles=$m_article->all();				$this->narticles=array();				foreach($this->articles as $article){			$this->article = $m_article->intro($article); 			$this->narticles[]=$this->article;		}	}	protected function OnOutput(){		$vars = array('articles'=>$this->narticles);				$this->content=$this->Template('v/V_Index.php',$vars);		parent::OnOutput();	}}?>