<?phpclass C_New extends C_Base{	protected $article_title;		// заголовок статьи	protected $article_content;		// содержание статьи	//	// Конструктор.	//	function __construct()	{			}		//	// Виртуальный обработчик запроса.	//	protected function OnInput()	{		$m_article= M_Article::Instance();				$this->title = "Blog OOP ";		$this->title2 = $this->title2.":: New Article ";				if($this->IsPost())		{			//if ($m_article->create_new(htmlspecialchars($_POST['title']), htmlspecialchars($_POST['content'])))			if ($m_article->create_new(($_POST['title']),($_POST['content'])))			{					header('Location: index.php?c=editor');					die();			}						$this->article_title = $_POST['title'];			$this->article_content = $_POST['content'];		}				else		{			$this->article_title = '';			$this->article_content = '';		}			}		//	// Виртуальный генератор HTML.	//		protected function OnOutput()	{		$vars = array('title' => $this->article_title, 'content' => $this->article_content);			$this->content = $this->Template('v/V_New.php', $vars);						parent::OnOutput();	}	}