<?php//include_once('m/startup.php');//include_once('m/M_Article.php');header('Content-type: text/html; charset=windows-1251');function __autoload($classname){	//startup();	//Разбивает строку на подстроки	$type = explode('_',$classname);	switch($type[0]){		case 'C':			include_once('c/'.$classname.'.php');			break;		case 'M':			include_once('m/'.$classname.'.php');			break;		}}switch ($_GET['c']){case 'edit':	$controller = new C_Edit();	break;case 'editor':	$controller = new C_Editor();	break;case 'new':	$controller = new C_New();	break;case 'article':	$controller = new C_Article();	break;	default:	$controller = new C_IndexArticle();}$controller->Request();?>